import React from 'react';
import { Breadcrumbs, Link, Box, Typography, Grid, Paper, Avatar } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ItemTypes = () => {

  const navigate = useNavigate();

  const items = [
    { name: 'Tin', qty: 25548 },
    { name: 'Env', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 }, 
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
    { name: 'Tin', qty: 25548 },
  ];

  // Function to handle item click
  const handleItemClick = (item) => {
    navigate('/ExcessInventoryItems', { state: { item } });
  };

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', p: 2, pt: 1 }}>

      {/* Header */}
      <Paper sx={{ py: 2, mb: 1, bgcolor: 'background.bg1', display: 'flex', alignItems: 'center', pl: 2 }}>
        <Typography variant="h4" fontWeight="bold" sx={{ transition: 'all 1s ease-in-out' }}>
          Aging Packing Materials Stock Management System
        </Typography>
      </Paper>

      {/* Item List */}
      <Paper sx={{ bgcolor: 'white', boxShadow: 3, borderRadius: 2, p: 2 }}>
        
        <Typography variant="h6" component="div" fontWeight="bold" mb={3} sx={{ textAlign: 'left' }}>
          Item Type
        </Typography>
        
        <Grid container spacing={2} sx={{ maxHeight: '73vh', overflowY: 'auto', transition: 'all 0.5s ease-in-out' }}>
          {items.map((item, index) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
              <Paper
                onClick={() => handleItemClick(item)}
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  p: 2,
                  borderRadius: 2,
                  boxShadow: 3,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease-in-out',
                  '&:hover': {
                    bgcolor: 'grey.400',
                    transform: 'scale(0.95)',
                  },
                }}
              >
                <Avatar
                  sx={{
                    width: 96,
                    height: 96,
                    bgcolor: 'grey.300',
                    mb: 2,
                  }}
                />
                <Typography variant="h6" component="div" fontWeight="bold">
                  {item.name}
                </Typography>
                <Typography color="textSecondary">
                  Qty - {item.qty}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Box>
  );
};

export default ItemTypes;
