import React,{ useState, useEffect } from 'react'
import axios from 'axios';
import { Breadcrumbs, Link, Box, Typography, Paper, Grid, Checkbox,
     Button, LinearProgress, IconButton, Collapse } from '@mui/material';
import { useLocation, Link as RouterLink } from 'react-router-dom';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown'; 
import LoadingBackdrop from './status/LoadingBackdrop';
import ErrorMessage from './status/ErrorMessage';
import { styled } from '@mui/material/styles';
import ReportGmailerrorredIcon from '@mui/icons-material/ReportGmailerrorred';

const StyledButton = styled(Button)(({ theme }) => ({
    backgroundColor: theme.palette.primary.blue,
    color: theme.palette.common.white,
    '&:hover': {
      backgroundColor: theme.palette.secondary.blue1,
    },
    padding: theme.spacing(1, 3),
    borderRadius: theme.shape.borderRadius,
    textTransform: 'none',
    fontWeight: 'bold',
  }));

const ProductList = () => {
    // Updated products array to include percentages
    const products = [
        { name: 'Product 1', raw: 50, packing: 40, consul: 30, 
            details: 'Product 1 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.', 
         },
        { name: 'Product 2', raw: 60, packing: 50, consul: 40,
            details: 'Product 2 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 3', raw: 70, packing: 60, consul: 50,
            details: 'Product 3 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 4', raw: 80, packing: 70, consul: 60,
            details: 'Product 4 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 5', raw: 90, packing: 80, consul: 70,
            details: 'Product 5 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 6', raw: 100, packing: 90, consul: 80,
            details: 'Product 6 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 7', raw: 10, packing: 100, consul: 90,
            details: 'Product 7 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 8', raw: 20, packing: 10, consul: 100,
            details: 'Product 8 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 9', raw: 30, packing: 20, consul: 10,
            details: 'Product 9 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 16', raw: 40, packing: 30, consul: 20,
            details: 'Product 10 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 11', raw: 50, packing: 40, consul: 30,
            details: 'Product 11 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },

    ];

    const [productList, setProductList] = useState([]); // Initialized to an empty array
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedProduct, setSelectedProduct] = useState(null);

    const handleDropdownClick = (index) => {
        setSelectedProduct(selectedProduct === index ? null : index); // Toggle selected product
    };

    

    const location = useLocation();
    const selectedItem = location.state?.item;
    const selectedCategory = location.state?.selectedItem;
    console.log(selectedItem);

    const fetchProductList = async () => {
        try {
          const response = await axios.get(`http://localhost:5200/api/dashboard/operations/agingInventory/item/productListByItem?itemCode=${selectedItem}`);
          setProductList(response.data.productList || []); // Handle the case when data is not available
          setLoading(false);
        } catch (err) {
          setError(err.message);
          setLoading(false);
        }
      };

      useEffect(() => {
        if (selectedItem) {
            fetchProductList();
        }
      }, [selectedItem]);
    
      if (loading) {
        return (
            <LoadingBackdrop loading={loading} />
        );
      }
    
      if (error) {
        return <ErrorMessage error={error} />;
      }

    return (
        <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', p: 2, pt: 1 }}>
            {/* Header */}
            <Paper sx={{ py: 2, mb: 1, bgcolor: 'primary.blue', display: 'flex', alignItems: 'center', pl: 2 }}>
                <Typography variant="h4" fontWeight="bold" sx={{ transition: 'all 1s ease-in-out' }}>
                    Aging Packing Materials Stock Management System
                </Typography>
            </Paper>

            {/* Page Content */}
            <Paper sx={{ bgcolor: 'white', boxShadow: 3, borderRadius: 2, p: 2 }}>
                {/* Breadcrumbs */}
                <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 2 }}>
                    <Link underline="hover" color="inherit" component={RouterLink} to="/">
                        Item Types
                    </Link>
                    <Link underline="hover" color="inherit" component={RouterLink} to="/excessInventoryItems" state={{ item:selectedCategory }}>
                        Excess Inventory Items
                    </Link>
                    <Typography color="text.primary">Product List</Typography>
                </Breadcrumbs>

                {selectedItem && (
                <Box mb={2}>
                    <Typography component="div" fontWeight="bold" mb={2} sx={{fontSize: 16, color: '#757575'}}>Excess Inventory: <span style={{color: '#000'}}>{selectedItem}</span></Typography>
            </Box>
                )}

                <Grid container spacing={2} alignItems="center" sx={{maxHeight: '62vh', overflowY: 'auto', transition: 'all 0.5s ease-in-out' }}>
                    {/* Header */}
                    <Grid item xs={1} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1, height: '40px' }}></Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Product List</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Raw Materials</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Packing Materials</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Consul Materials</Typography>
                    </Grid>
                    <Grid item xs={1} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1,height: '40px' }}></Grid>

                    {/* Product Rows */}
                    
                    {productList && productList.length > 0 ? (
    productList.map((product, index) => (
        <React.Fragment key={index}>
            <Grid item xs={1}>
                <Checkbox />
            </Grid>
            <Grid item xs={2.5} container alignItems="center">
                <Typography>{product.item}</Typography>
                <Typography fontSize={12}>{product.description}</Typography>
            </Grid>

            {/* Progress Bars with percentages */}
            <Grid item xs={2.5}>
                <LinearProgress variant="determinate" value={50} />
                <Typography>{50}%</Typography>
            </Grid>
            <Grid item xs={2.5}>
                <LinearProgress variant="determinate" value={35} />
                <Typography>{35}%</Typography>
            </Grid>
            <Grid item xs={2.5}>
                <LinearProgress variant="determinate" value={85} />
                <Typography>{85}%</Typography>
            </Grid>
            <Grid xs={1}>
                <IconButton onClick={() => handleDropdownClick(index)} 
                    sx={{ transform: selectedProduct === index ? 'rotate(180deg)' : 'rotate(0deg)',
                          transition: 'all 0.3s ease-in-out' }}>
                    <ArrowDropDownIcon />
                </IconButton>
            </Grid>

            {/* Product Details - Conditionally rendered below the row */}
            {selectedProduct === index && (
                <Grid item xs={12}>
                    <Collapse sx={{transition: 'all 0.1s ease-in-out' }} in={selectedProduct === index} timeout="auto" unmountOnExit>
                        <Box sx={{ p: 2, pl: 11, backgroundColor: 'secondary.blue' }}>
                            <Typography variant="body2">
                                <span style={{ fontWeight: 'bold' }}>Material quantity: </span>{product.matl_qty}
                            </Typography>
                            <Typography variant="body2">
                                <span style={{ fontWeight: 'bold' }}>Units per inner carton: </span>{product.UnitsPerInner}
                            </Typography>
                            <Typography variant="body2">
                                <span style={{ fontWeight: 'bold' }}>Inner cartons per retail carton: </span>{product.InnerPerRC}
                            </Typography>
                            <Typography variant="body2">
                                <span style={{ fontWeight: 'bold' }}>Retail carton per Master carton: </span>{product.RCPerMC}
                            </Typography>
                        </Box>
                    </Collapse>
                </Grid>
            )}
        </React.Fragment>
    ))
) : (
    <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'warning.main' }}>
        <ReportGmailerrorredIcon sx={{ mr: 1 }} />
        <Typography>No data found</Typography>
    </Box>
)}

                </Grid>
                {/* Compare Button */}
                <Grid item xs={12} sx={{ textAlign: 'right', mr: 6, mt: 1 }}>
                    <StyledButton variant="contained">Compare</StyledButton>
                </Grid>
            </Paper>
        </Box>
    );
}

export default ProductList;
