import React, { useState } from 'react';
import axios from 'axios';
import { Breadcrumbs, Link, Box, Typography, Paper, TextField, Table, TableBody, 
        TableCell, TableContainer, TableHead, TableRow, TableSortLabel } from '@mui/material';
import { useLocation, Link as RouterLink, useNavigate } from 'react-router-dom';

const ExcessInventoryItems = () => {

  // const items = [
  //   { name: 'Env a', qty: 1000, value: 1000, agedMonths: 40 },
  //   { name: 'Env b', qty: 680, value: 2000, agedMonths: 10 },
  //   { name: 'Env c', qty: 1260, value: 1390, agedMonths: 14 },
  //   { name: 'Env d', qty: 600, value: 2100, agedMonths: 19 },
  //   { name: 'Env e', qty: 250, value: 600, agedMonths: 32 },
  //   { name: 'Env f', qty: 400, value: 690, agedMonths: 12 },
  //   { name: 'Env g', qty: 430, value: 200, agedMonths: 15 },
  //   { name: 'Env h', qty: 170, value: 1380, agedMonths: 30 },
  //   { name: 'Env i', qty: 80, value: 2170, agedMonths: 26 },
  //   { name: 'Env j', qty: 1100, value: 1300, agedMonths: 24 },
  //   { name: 'Env k', qty: 419, value: 900, agedMonths: 20 },
  // ];

  const [orderDirection, setOrderDirection] = useState('asc');
  const [orderBy, setOrderBy] = useState('name');
  const [searchQuery, setSearchQuery] = useState('');

  const navigate = useNavigate();

  const handleSort = (property) => {
    const isAsc = orderBy === property && orderDirection === 'asc';
    setOrderDirection(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const filteredItems = items.filter((item) =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.qty.toString().includes(searchQuery) ||
    item.value.toString().includes(searchQuery) ||
    item.agedMonths.toString().includes(searchQuery)
  );

  const sortedItems = filteredItems.sort((a, b) => {
    if (orderDirection === 'asc') {
      return a[orderBy] > b[orderBy] ? 1 : -1;
    } else {
      return a[orderBy] < b[orderBy] ? 1 : -1;
    }
  });

  const handleRowClick = (row) => {
    navigate('/ProductList', { state: { item: row } });
  };

  const location = useLocation();
  const selectedItem = location.state?.item; //selected item
  const [excessInventories, setExcessInventories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchExcessInventoryItems = async () => {
    try {
      const response = await axios.get(`http://localhost:5200/api/dashboard/operations/agingInventory/subItems/${selectedItem.name}`);
      setExcessInventories(response.data.items); // Assuming subItems is the list returned from the API
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  // Fetch items when the component mounts or when `selectedItem` changes
  useEffect(() => {
    if (selectedItem) {
      fetchSubItems();
    }
  }, [selectedItem]);

  if (loading) {
    return <Typography>Loading...</Typography>;
  }

  if (error) {
    return <Typography>Error: {error}</Typography>;
  }

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', p: 2, pt: 1 }}>
      {/* Header */}
      <Paper sx={{ py: 2, mb: 1, bgcolor: 'background.bg1', display: 'flex', alignItems: 'center', pl: 2 }}>
        <Typography variant="h4" fontWeight="bold" sx={{ transition: 'all 1s ease-in-out' }}>
          Aging Packing Materials Stock Management System
        </Typography>
      </Paper>

      {/* Page Content */}
      <Paper sx={{ bgcolor: 'white', boxShadow: 3, borderRadius: 2, p: 2 }}>
        {/* Breadcrumbs */}
        <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 1 }}>
          <Link underline="hover" color="inherit" component={RouterLink} to="/">
            Item Types
          </Link>
          <Typography color="text.primary">Excess Inventory Items</Typography>
        </Breadcrumbs>

        {/* Header and Search bar */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 0 }}>
          <Typography variant="h6" component="div" fontWeight="bold">
            Excess Inventory Items
          </Typography>
          <TextField
            variant="outlined"
            placeholder="Search"
            size="small"
            sx={{ width: 300 }}
            value={searchQuery} // Set the value of TextField to searchQuery
            onChange={(e) => setSearchQuery(e.target.value)} // Update searchQuery on typing
          />
        </Box>

        {item && (
          <Box mt={0}>
            <Typography variant="h6">{selectedItem.name}</Typography>
          </Box>
        )}

        {/* Table */}
        <TableContainer component={Paper} sx={{ mt: 0, maxHeight: '64vh' }}>
          <Table sx={{ minWidth: 650, tableLayout: 'auto' }} aria-label="excess inventory table" stickyHeader>
            <TableHead>
              <TableRow sx={{ bgcolor: 'background.bg2', fontWeight: 'bold'}}>
                <TableCell sx={{ fontWeight: 'bold', fontSize: '1rem',}}>
                  
                  <TableSortLabel
                    active={orderBy === 'name'}
                    direction={orderBy === 'name' ? orderDirection : 'asc'}
                    onClick={() => handleSort('name')}
                  > 
                  Name
                  </TableSortLabel>
                </TableCell>
                <TableCell align="right" sx={{ fontWeight: 'bold', fontSize: '1rem'}}>
                  <TableSortLabel
                    active={orderBy === 'qty'}
                    direction={orderBy === 'qty' ? orderDirection : 'asc'}
                    onClick={() => handleSort('qty')}
                  >
                    Qty
                  </TableSortLabel>
                </TableCell>
                <TableCell align="right" sx={{ fontWeight: 'bold', fontSize: '1rem'}}>
                  <TableSortLabel
                    active={orderBy === 'value'}
                    direction={orderBy === 'value' ? orderDirection : 'asc'}
                    onClick={() => handleSort('value')}
                  >
                    Value
                  </TableSortLabel>
                </TableCell>
                <TableCell align="right" sx={{ fontWeight: 'bold', fontSize: '1rem'}}>
                  <TableSortLabel
                    active={orderBy === 'agedMonths'}
                    direction={orderBy === 'agedMonths' ? orderDirection : 'asc'}
                    onClick={() => handleSort('agedMonths')}
                  >
                    No of aged months
                  </TableSortLabel>
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {sortedItems.map((row, index) => (
                <TableRow key={index} hover onClick={() => handleRowClick(row)} sx={{ cursor: 'pointer' }}>
                  <TableCell component="th" scope="row" >{row.name}</TableCell>
                  <TableCell align="right">{row.qty}</TableCell>
                  <TableCell align="right">{row.value}</TableCell>
                  <TableCell align="right">{row.agedMonths}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Box>
  );
};

export default ExcessInventoryItems;
