// src/components/ItemCard.jsx
import React from 'react';
import { Card, CardActionArea, CardContent, Typography } from '@mui/material';
import { styled } from '@mui/system';

const StyledCard = styled(Card)(({ theme }) => ({
  '&:hover': {
    transform: 'scale(1.05)',
    boxShadow: theme.shadows[6],
  },
  transition: 'transform 0.3s, box-shadow 0.3s',
}));

const ItemCard = ({ item, onSelect }) => (
  <StyledCard onClick={() => onSelect(item)}>
    <CardActionArea>
      <img src={item.image} alt={item.name} style={{ width: '100%', height: '150px', objectFit: 'cover' }} />
      <CardContent>
        <Typography variant="h6">{item.name}</Typography>
        <Typography variant="body2" color="text.secondary">Quantity: {item.quantity}</Typography>
      </CardContent>
    </CardActionArea>
  </StyledCard>
);

export default ItemCard;
