import React,{ useState, useEffect } from 'react'
import axios from 'axios';
import { Breadcrumbs, Link, Box, Typography, Paper, Grid, Checkbox,
     Button, LinearProgress, IconButton, Collapse, TextField } from '@mui/material';
import { useLocation, Link as RouterLink } from 'react-router-dom';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown'; 
import ReportGmailerrorredIcon from '@mui/icons-material/ReportGmailerrorred';
import LoadingBackdrop from './status/LoadingBackdrop';
import ErrorMessage from './status/ErrorMessage';
import { styled } from '@mui/material/styles';
import { LoadingButton } from '@mui/lab';

const StyledButton = styled(Button)(({ theme }) => ({
    backgroundColor: theme.palette.primary.blue,
    color: theme.palette.common.white,
    '&:hover': {
      backgroundColor: theme.palette.secondary.blue1,
    },
    padding: theme.spacing(1, 3),
    borderRadius: theme.shape.borderRadius,
    textTransform: 'none',
    fontWeight: 'bold',
  }));

  const StyledLoadingButton = styled(LoadingButton)(({ theme }) => ({
    backgroundColor: theme.palette.primary.blue,
    color: theme.palette.common.white,
    '&:hover': {
      backgroundColor: theme.palette.secondary.blue1,
    },
    padding: theme.spacing(1, 2), // Adjust padding to ensure even spacing
    borderRadius: theme.shape.borderRadius,
    textTransform: 'none',
    fontWeight: 'bold',
    minWidth: '90px',
    maxWidth: '110px',
    boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)', // Apply box shadow evenly across the button
    width: '100%', // Ensures the button takes full width
    '& .MuiLoadingButton-loadingIndicator': {
      right: theme.spacing(1), // Adjust position of the loading icon
    },
  }));
  

const ProductList = () => {


    const handleRefresh = () => {
        if (selectedItem && targetProductQty) {
            setIsRefreshing(true);
            fetchProductList().finally(() => {
                setIsRefreshing(false);
            });
        }
    };

    const [productList, setProductList] = useState([]); // Initialized to an empty array
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [targetProductQty, setTargetProductQty] = useState('100');
    const [isRefreshing, setIsRefreshing] = useState(false);

    const handleDropdownClick = (index) => {
        setSelectedProduct(selectedProduct === index ? null : index); // Toggle selected product
    };

    const handleTargetQtyChange = (event) => {
        const value = event.target.value;
        // Only allow numeric input
        if (value === '' || /^[0-9]+$/.test(value)) {
            setTargetProductQty(value);
        }
    }; 

    const location = useLocation();
    const selectedItem = location.state?.item.ItenCode;
    const selectedItemOnHandQuantity = location.state?.item.qty_on_hand;
    const selectedItemAllocatedJobQuantity = location.state?.item.qty_allocjob;
    const selectedItemAvailableQuantity = location.state?.item.available_qty;
    const selectedCategory = location.state?.selectedItem;

    const fetchProductList = async () => {
        try {
            const response = await axios.get(`http://localhost:5200/api/dashboard/operations/agingInventory/item/productListByItem`, {
                params: {
                    itemCode: selectedItem,
                    targetQty: targetProductQty
                }
            });
          setProductList(response.data.productList || []); // Handle the case when data is not available
          setLoading(false);
        } catch (err) {
          setError(err.message);
          setLoading(false);
        }
      };

      useEffect(() => {
        if (selectedItem) {
            fetchProductList();
        }
      }, [selectedItem]);
    
      if (loading) {
        return (
            <LoadingBackdrop loading={loading} />
        );
      }
    
      if (error) {
        return <ErrorMessage error={error} />;
      }

    return (
        <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', p: 2, pt: 1 }}>
            {/* Header */}
            <Paper sx={{ py: 2, mb: 1, bgcolor: 'primary.blue', display: 'flex', alignItems: 'center', pl: 2 }}>
                <Typography variant="h4" fontWeight="bold" sx={{ transition: 'all 1s ease-in-out' }}>
                    Aging Packing Materials Stock Management System
                </Typography>
            </Paper>

            {/* Page Content */}
            <Paper sx={{ bgcolor: 'white', boxShadow: 3, borderRadius: 2, p: 2 }}>
                {/* Breadcrumbs */}
                <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 2 }}>
                    <Link underline="hover" color="inherit" component={RouterLink} to="/">
                        Item Types
                    </Link>
                    <Link underline="hover" color="inherit" component={RouterLink} to="/excessInventoryItems" state={{ item:selectedCategory }}>
                        Excess Inventory Items
                    </Link>
                    <Typography color="text.primary">Product List</Typography>
                </Breadcrumbs>

                {selectedItem && (
                <Box mb={2}>
                    <Grid container spacing={2} alignItems="center">
                        <Grid item xs={2.5}>
                            <Typography component="div" fontWeight="bold" sx={{fontSize: 16, color: '#757575'}}>
                                Inventory: <span style={{color: '#000'}}>{selectedItem}</span>
                            </Typography>
                        </Grid>
                        <Grid item xs={2}>
                            <Typography component="div" fontWeight="bold" sx={{fontSize: 16, color: '#757575'}}>
                                On Hand Qty: <span style={{color: '#000'}}>{selectedItemOnHandQuantity}</span>
                            </Typography>
                        </Grid>
                        <Grid item xs={2}>
                            <Typography component="div" fontWeight="bold" sx={{fontSize: 16, color: '#757575'}}>
                                Job Alctd. Qty: <span style={{color: '#000'}}>{selectedItemAllocatedJobQuantity}</span>
                            </Typography>
                        </Grid>
                        <Grid item xs={2}>
                            <Typography component="div" fontWeight="bold" sx={{fontSize: 16, color: '#757575'}}>
                                Available Qty: <span style={{color: '#000'}}>{selectedItemAvailableQuantity}</span>
                            </Typography>
                        </Grid>
                        <Grid item xs={3.5}>
                            <Box display="flex" alignItems="center">
                                <Typography component="div" fontWeight="bold" sx={{fontSize: 16, color: '#757575', mr: 1}}>
                                    Target Product Qty:
                                </Typography>
                                <TextField
                                    value={targetProductQty || '100'}
                                    onChange={handleTargetQtyChange}
                                    type="text"
                                    inputProps={{
                                        inputMode: 'numeric',
                                        pattern: '[0-9]*'
                                    }}
                                    size='small'
                                    placeholder="100"
                                    sx={{ flexGrow: 0, width: '100px', marginRight: '2px' }}
                                />
                                {/* <StyledLoadingButton
                                    size="small"
                                    variant="outlined"
                                    onClick={handleRefresh}
                                    loading={isRefreshing}
                                    loadingIndicator="Loading…"
                                >
                                    Fetch data
                                </StyledLoadingButton> */}
                                <StyledLoadingButton
                                    size="small"
                                    onClick={handleRefresh}
                                    loading={isRefreshing}
                                    loadingPosition="end" 
                                    variant="contained"
                                >
                                Fetch Data
                                </StyledLoadingButton>
                            </Box>
                        </Grid>
                    </Grid>
            </Box>
                )}

                <Grid container spacing={2} alignItems="center" sx={{maxHeight: '60vh', overflowY: 'auto', transition: 'all 0.5s ease-in-out' }}>
                    {/* Header */}
                    <Grid item xs={1} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1, height: '40px' }}></Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Product List</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Raw Materials</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Packing Materials</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Consul Materials</Typography>
                    </Grid>
                    <Grid item xs={1} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1,height: '40px' }}></Grid>

                    {/* Product Rows */}
                    {productList.map((product, index) => (
                        <React.Fragment key={index}>
                            <Grid item xs={1}>
                                <Checkbox />
                            </Grid>
                            <Grid item xs={2.5} container alignItems="center">
                                <Typography>{product.item}</Typography>
                                <Typography fontSize={12}>{product.description}</Typography>
                            </Grid>

                            {/* Progress Bars with percentages */}
                            <Grid item xs={2.5}>
                                <LinearProgress variant="determinate" value={product.percentages.Raw} />
                                <Typography>{product.percentages.Raw}%</Typography>
                            </Grid>
                            <Grid item xs={2.5}>
                                <LinearProgress variant="determinate" value={product.percentages.Packing} />
                                <Typography>{product.percentages.Packing}%</Typography>
                            </Grid>
                            <Grid item xs={2.5}>
                                <LinearProgress variant="determinate" value={product.percentages.Consul} />
                                <Typography>{product.percentages.Consul}%</Typography>
                            </Grid>
                            <Grid xs={1}>
                                <IconButton onClick={() => handleDropdownClick(index)} 
                                        sx={{ transform: selectedProduct === index ? 'rotate(180deg)' : 'rotate(0deg)',
                                            transition: 'all 0.3s ease-in-out' }}>
                                    <ArrowDropDownIcon />
                                </IconButton>
                            </Grid>

                            {/* Product Details - Conditionally rendered below the row */}
                            {selectedProduct === index && (
                                <Grid item xs={12}>
                                    <Collapse sx={{transition: 'all 0.1s ease-in-out' }} in={selectedProduct === index} timeout="auto" unmountOnExit>
                                        <Box sx={{ p: 2,pl:11, backgroundColor: 'secondary.blue' }}>
                                            <Typography variant="body2">
                                                <span style={{ fontWeight: 'bold' }}>Marerial quantity: </span>{product.matl_qty}
                                            </Typography>
                                            <Typography variant="body2">
                                                <span style={{ fontWeight: 'bold' }}>Maximum producible master cartons (related to the selected inventory item): </span>{Math.max(0, Math.floor(selectedItemAvailableQuantity / product.matl_qty))}
                                            </Typography>
                                            <Typography variant="body2">
                                                <span style={{ fontWeight: 'bold' }}>Units per inner carton: </span>{product.UnitsPerInner}
                                            </Typography>
                                            <Typography variant="body2">
                                                <span style={{ fontWeight: 'bold' }}>Inner cartons per retail carton: </span>{product.InnerPerRC}
                                            </Typography>
                                            <Typography variant="body2">
                                                <span style={{ fontWeight: 'bold' }}>Retail carton per Master carton: </span>{product.RCPerMC}
                                            </Typography>
                                        </Box>
                                    </Collapse>
                                </Grid>
                            )}
                        </React.Fragment>
                    ))}
                </Grid>
                {/* Compare Button */}
                <Grid item xs={12} sx={{ textAlign: 'right', mr: 6, mt: 1 }}>
                    <StyledButton variant="contained">Compare</StyledButton>
                </Grid>
            </Paper>
        </Box>
    );
}

export default ProductList;
