import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#c7c5c5', // Blue
    },
    secondary: {
      main: '#dc004e', // Pink
    },
    background: {
      default: '#f5f5f5', // Light gray background
      bg1: '#b0bec5',
      bg2: '#daecf5'
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
    h4: {
      fontWeight: 700,
    },
    h5: {
      fontWeight: 500,
    },
    body2: {
      fontSize: '0.875rem',
    },
    heading: {
        fontSize: '30px'
    },
    tableHeader: {
      fontSize: '25px'
    }
  },
  TableCell: {
    th1:{
      fontSize: '26px',
      fontWeight: 'bold'
    }
  },
});

export default theme;
