import React,{ useState } from 'react'
import { Breadcrumbs, Link, Box, Typography, Paper, Grid, Checkbox,
     Button, LinearProgress, IconButton } from '@mui/material';
import { useLocation, Link as RouterLink } from 'react-router-dom';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';  

const ProductList = () => {
    // Updated products array to include percentages
    const products = [
        { name: 'Product 1', raw: 50, packing: 40, consul: 30, 
            details: 'Product 1 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.', 
         },
        { name: 'Product 2', raw: 60, packing: 50, consul: 40,
            details: 'Product 2 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 3', raw: 70, packing: 60, consul: 50,
            details: 'Product 3 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 4', raw: 80, packing: 70, consul: 60,
            details: 'Product 4 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 5', raw: 90, packing: 80, consul: 70,
            details: 'Product 5 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 6', raw: 100, packing: 90, consul: 80,
            details: 'Product 6 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 7', raw: 10, packing: 100, consul: 90,
            details: 'Product 7 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 8', raw: 20, packing: 10, consul: 100,
            details: 'Product 8 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 9', raw: 30, packing: 20, consul: 10,
            details: 'Product 9 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 16', raw: 40, packing: 30, consul: 20,
            details: 'Product 10 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },
        { name: 'Product 11', raw: 50, packing: 40, consul: 30,
            details: 'Product 11 details: Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
         },

    ];
    const [selectedProduct, setSelectedProduct] = useState(null);

    const handleDropdownClick = (index) => {
        setSelectedProduct(selectedProduct === index ? null : index); // Toggle selected product
    };

    const location = useLocation();
    const selectedItem = location.state?.item;
    

    return (
        <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', p: 2, pt: 1 }}>
            {/* Header */}
            <Paper sx={{ py: 2, mb: 1, bgcolor: 'primary.blue', display: 'flex', alignItems: 'center', pl: 2 }}>
                <Typography variant="h4" fontWeight="bold" sx={{ transition: 'all 1s ease-in-out' }}>
                    Aging Packing Materials Stock Management System
                </Typography>
            </Paper>

            {/* Page Content */}
            <Paper sx={{ bgcolor: 'white', boxShadow: 3, borderRadius: 2, p: 2 }}>
                {/* Breadcrumbs */}
                <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 3 }}>
                    <Link underline="hover" color="inherit" component={RouterLink} to="/">
                        Item Types
                    </Link>
                    <Link underline="hover" color="inherit" component={RouterLink} to="/excessInventoryItems">
                        Excess Inventory Items
                    </Link>
                    <Typography color="text.primary">Product List</Typography>
                </Breadcrumbs>

                <Grid container spacing={2} alignItems="center" sx={{maxHeight: '69vh', overflowY: 'auto', transition: 'all 0.5s ease-in-out' }}>
                    {/* Header */}
                    <Grid item xs={1} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1, height: '40px' }}></Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Product List</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Raw Materials</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Packing Materials</Typography>
                    </Grid>
                    <Grid item xs={2.5} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                        <Typography variant="h6">Consul Materials</Typography>
                    </Grid>
                    <Grid item xs={1} sx={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1,height: '40px' }}></Grid>

                    {/* Product Rows */}
                    {products.map((product, index) => (
                        <React.Fragment key={index}>
                            <Grid item xs={1}>
                                <Checkbox />
                            </Grid>
                            <Grid item xs={2.5} container alignItems="center">
                                <Box
                                    sx={{
                                        width: '40px',
                                        height: '40px',
                                        backgroundColor: '#e0e0e0',
                                        marginRight: '10px',
                                        display: 'inline-block',
                                    }}
                                >
                                    {/* Placeholder for product image */}
                                </Box>
                                <Typography>{product.name}</Typography>
                            </Grid>

                            {/* Progress Bars with percentages */}
                            <Grid item xs={2.5}>
                                <LinearProgress variant="determinate" value={product.raw} />
                                <Typography>{product.raw}%</Typography>
                            </Grid>
                            <Grid item xs={2.5}>
                                <LinearProgress variant="determinate" value={product.packing} />
                                <Typography>{product.packing}%</Typography>
                            </Grid>
                            <Grid item xs={2.5}>
                                <LinearProgress variant="determinate" value={product.consul} />
                                <Typography>{product.consul}%</Typography>
                            </Grid>
                            <Grid xs={1}>
                                <IconButton onClick={() => handleDropdownClick(index)}>
                                    <ArrowDropDownIcon />
                                </IconButton>
                            </Grid>

                            {/* Product Details - Conditionally rendered below the row */}
                            {selectedProduct === index && (
                                <Grid item xs={12}>
                                    <Box sx={{ p: 2, backgroundColor: '#f5f5f5' }}>
                                        <Typography variant="body2">{product.details}</Typography>
                                    </Box>
                                </Grid>
                            )}
                        </React.Fragment>
                    ))}
                </Grid>
                {/* Compare Button */}
                <Grid item xs={12} sx={{ textAlign: 'right', mr: 6 }}>
                    <Button variant="contained">Compare</Button>
                </Grid>
            </Paper>
        </Box>
    );
}

export default ProductList;
